import React from 'react';
import { View, Text, StyleSheet, Pressable, TextInput, ScrollView } from 'react-native';

const EMPLOYEES = [
  { id: 1, name: 'Amira Chen', role: 'Product Designer', status: 'Active posting', location: 'Prague' },
  { id: 2, name: 'Luis Ortega', role: 'Senior Backend', status: 'Interviewing', location: 'Berlin' },
  { id: 3, name: 'max govindaraju', role: 'HR Business Partner', status: 'New hire', location: 'Remote' },
  { id: 4, name: 'Jonas Eriksen', role: 'Mobile Engineer', status: 'Internal move', location: 'Copenhagen' }
];

const theme = {
  bg: '#0E3A2F',
  card: '#0b2f26',
  muted: '#b2e6c7',
  primary: '#78FAAE',
  accent: '#78FAAE',
  border: '#135240'
};

export default function App() {
  const [stage, setStage] = React.useState('login'); // login | hr-dashboard | employee-dashboard
  const [role, setRole] = React.useState('HR');
  const [name, setName] = React.useState('');
  const [password, setPassword] = React.useState('');
  const passwordRef = React.useRef(null);

  const startLogin = () => {
    if (role === 'HR') return setStage('hr-dashboard');
    setStage('employee-dashboard');
  };

  const logout = () => setStage('login');

  const goHome = () => setStage(role === 'HR' ? 'hr-dashboard' : 'employee-dashboard');

  const renderHeader = (title, subtitle) => (
    <View style={styles.header}>
      <View>
        <Text style={styles.kicker}>Internal Job Management</Text>
        <Text style={styles.pageTitle}>{title}</Text>
        <Text style={styles.pageSubtitle}>{subtitle}</Text>
      </View>
      {stage !== 'login' && (
        <View style={{ flexDirection: 'row', gap: 8 }}>
          <Pressable onPress={goHome} style={[styles.pill, { borderColor: theme.primary }]}>
            <Text style={[styles.pillText, { color: theme.primary }]}>{role === 'HR' ? 'HR' : 'Employee'} dashboard</Text>
          </Pressable>
          <Pressable onPress={logout} style={[styles.pill, { borderColor: '#f87171' }]}>
            <Text style={[styles.pillText, { color: '#f87171' }]}>Logout</Text>
          </Pressable>
        </View>
      )}
    </View>
  );

  const StatCard = ({ label, value, hint, color }) => (
    <View style={[styles.card, { borderColor: color || theme.border, backgroundColor: theme.card }]}>
      <Text style={[styles.statLabel, { color: theme.muted }]}>{label}</Text>
      <Text style={[styles.statValue, { color: color || '#fff' }]}>{value}</Text>
      {!!hint && <Text style={styles.statHint}>{hint}</Text>}
    </View>
  );

  const Tile = ({ employee }) => (
    <View style={styles.tile}>
      <View style={styles.tileRow}>
        <Text style={styles.tileName}>{employee.name}</Text>
        <Text style={styles.statusBadge}>{employee.status}</Text>
      </View>
      <Text style={styles.tileRole}>{employee.role}</Text>
      <Text style={styles.tileMeta}>{employee.location}</Text>
    </View>
  );

  const Login = () => (
    <View style={styles.loginWrapper}>
      <View style={styles.heroCard}>
        <Text style={styles.authTitle}>Authentification</Text>
        <TextInput
          value={name}
          placeholder="Username"
          placeholderTextColor="#c8f2da"
          onChangeText={setName}
          blurOnSubmit={false}
          autoCapitalize="none"
          returnKeyType="next"
          onSubmitEditing={() => passwordRef.current?.focus()}
          style={styles.input}
        />
        <TextInput
          value={password}
          placeholder="Password"
          placeholderTextColor="#c8f2da"
          secureTextEntry
          onChangeText={setPassword}
          blurOnSubmit={false}
          autoCapitalize="none"
          returnKeyType="done"
          ref={passwordRef}
          onSubmitEditing={startLogin}
          style={styles.input}
        />
        <View style={styles.roleToggle}>
          {['HR', 'Employee'].map(item => {
            const active = role === item;
            return (
              <Pressable
                key={item}
                onPress={() => setRole(item)}
                style={[styles.roleToggleButton, active && styles.roleToggleActive]}
              >
                <Text style={[styles.roleToggleText, active && styles.roleToggleTextActive]}>{item}</Text>
              </Pressable>
            );
          })}
        </View>
        <Pressable onPress={startLogin} style={styles.loginButton}>
          <Text style={styles.loginButtonText}>Login</Text>
        </Pressable>
      </View>
    </View>
  );

  const HrDashboard = () => (
    <View style={{ gap: 18 }}>
      {renderHeader('Dashboard HR', '')}
    </View>
  );

  const EmployeeDashboard = () => (
    <View style={{ gap: 18 }}>
      {renderHeader('Espace Collaborateur', '')}
    </View>
  );

  return (
    <ScrollView style={styles.app} contentContainerStyle={styles.container}>
      <View style={styles.bgBlobOne} />
      <View style={styles.bgBlobTwo} />
      {stage === 'login' && <Login />}
      {stage === 'hr-dashboard' && <HrDashboard />}
      {stage === 'employee-dashboard' && <EmployeeDashboard />}
      {stage === 'hr-dashboard' && <HrDashboard />}
    </ScrollView>
  );
}

const Pill = ({ text }) => (
  <View style={[styles.pill, { borderColor: theme.border }]}>
    <Text style={styles.pillText}>{text}</Text>
  </View>
);

const styles = StyleSheet.create({
  app: {
    backgroundColor: theme.bg
  },
  container: {
    minHeight: '100vh',
    paddingVertical: 32,
    paddingHorizontal: 20,
    gap: 20,
    position: 'relative'
  },
  bgBlobOne: {
    position: 'absolute',
    width: 320,
    height: 320,
    backgroundColor: '#0c473a',
    opacity: 0.35,
    borderRadius: 999,
    top: -40,
